import {USER_LOGIN, NOTIFICATIONS} from '../constants'
import {default as ax} from "axios";

export const userLogin =(data)=>{
    return {
        type:USER_LOGIN,
        data:data
    }
}

export const notifications =(data)=>{
    return {
        type:NOTIFICATIONS,
        data:data
    }
}

export const axios = ax.create({
    baseURL:`http://13.127.223.247:80/`
